import 'antd/dist/antd.css';
import '../App.css';
import axios from 'axios';
import { Link } from "react-router-dom";
import { Button, Input, Form, Radio, DatePicker,
  Select, Card, InputNumber, Checkbox, Upload, message } from 'antd';
import React, {  useState, useEffect } from 'react';
import TextArea from 'antd/lib/input/TextArea';
import reqwest from 'reqwest';


const { Option } = Select;



function Anketa(props) { 
  // Стейты для копирования файлов 
  const [fileDiplom, setFileDiplom ] = useState([])
  const [uploading, setUploading] = useState(false)
  // Функция для копирования файлов
  const handleUpload = () => {    
    const formDataDiplom = new FormData();   
    fileDiplom.forEach(file => {       
      formDataDiplom.append('file[]', file, file.name);           
    });   
    setUploading(true)  
    reqwest({
      url:'http://localhost:5001/upload',
      method: 'post',
      processData: false,
      data: formDataDiplom,     
      success: () => {        
        setUploading(false);
        setFileDiplom([]);       
        message.success({style: {marginTop: '20vh'},
        content:'Поздравляем! Ваша анкета успешно загружена',duration: 2});
      },
      error: () => {        
        setUploading(false);      
        message.error('upload failed.');
      },
    });
  };
  //предварительная обработка файла перед отправкой
  const beforeUpload = (file,b) => {    
    Object.defineProperty(file, 'name', {
      value: b,
      writable: true,
      enumerable: true,
      configurable: true
    });    
    if (file.type !== 'image/jpeg'  ) {
      message.error({content:`${file.name} не  jpg  файл !`,duration: 2});
    } else if( file.size > 2000000) {
      message.error({content:` Размер ${file.name} больше 2 мб !`,duration:2});
    } else {
      setFileDiplom([...fileDiplom, file]);
    }    
  } 
  // пропсы для отправки файла       
  const propDiplom = {    
    onRemove: file => {      
      const index = fileDiplom.indexOf(file);
      const newFileList = fileDiplom.slice();
      newFileList.splice(index, 1);       
      setFileDiplom(newFileList);       
    }
  }
  // расшифровка запроса на анкету
  const query = window.location.search.substring(1).split("=")[1]
  console.log( window.location.search)
  // определяем  выбранную анкету
  const anketaType = query === '1' ? 
    "Сбербанк Здоровье" : query === '2' ?  
    "врача из клиники партнера" : 
    "врача из регионального МО (для РГС)"


  const [anketa, setAnketa] = useState("")
  useEffect(()=>setAnketa(query),[query])  
  const [form] = Form.useForm()
  const [optWork, setOptWork] = useState([1])
  const [kurs, addKurs] = useState([1])
  // принимаем анкету с сервера 
  function GetAnkete(){
    axios.get(`https://jsonplaceholder.typicode.com/users`)
    .then(res =>
      { 
        console.log(Object.keys(res.data[0]));
        const s = Object.entries(res.data[0]);
        for( const ss of s ){         
          setFields([{name: [ss[0]], value: ss[1] }]);
          console.log(ss);
        };               
      }
    )
  }
  //отправляем анкету на сервер
  function ResiveAnkete(values) {    
    axios({
    method: 'post',    
    url: `https://jsonplaceholder.typicode.com/users`,
    data:{values}   
  })
  .then(res => {
    handleUpload();    
    console.log(res.data);
  })
    console.log("resive ankete")
  }
  const [fields, setFields] = useState()
  useEffect(()=> {
    if(query.length === 10){
    return GetAnkete()
    } 
  },[query])
  const optionsWithDisabled = [
    { label: 'Сбербанк здоровье', value: '1' },
    { label: 'врача из клиники партнера', value: '2' },
    { label: 'врача из регионального МО (для РГС)', disabled: true, value: '3'},
  ];
  //set type ankete
  const [typeAnkete, setTypeAnkete] = useState("0")
  const onChange4 = e => {
    console.log('radio4 checked', e.target.value);
    setTypeAnkete(e.target.value);
  };

  useEffect(()=>{
   if(query.length > 1) {setTypeAnkete("0")} else {setTypeAnkete(query)}
  },[query])

  return (
    <div className="App">     
      <Link to="/" className="App-header" title="на главную"/>
      {query.length > 5 && <>
        <h2>Выберите анкету для редактирования</h2>   
        <Radio.Group
            options={optionsWithDisabled}
            onChange={onChange4}
            size="large"
            value={typeAnkete}
            optionType="button"
            buttonStyle="solid"            
          />
        </>
      }
      { typeAnkete !=="0" && 
           <Form            
            layout="vertical"
            form={form}
            fields={fields}
            initialValues={{remember: true}}            
            onFinish={ResiveAnkete}
          >
          <h2>Анкета для трудоустройства в {anketaType}</h2>        
          <p style={{textAlign: 'left',color: 'red'}}>* обязательные для заполнения поля</p>
            {typeAnkete !== '1' ? 
              <Form.Item  name="nameMO" label="Название МО"                          
                rules={[{ required: true, message: 'Пожалуйста наберите название МО!',
                whitespace: true }]}              
              >
                <Input               
                  required
                  placeholder="название мо"
                  pattern="[А-Яа-я\s]{1,20}"
                  title="русскими буквами"                   
