import 'antd/dist/antd.css';
import '../App.css';
import axios from 'axios'
import { Link, useHistory } from "react-router-dom";
import { Button, Input, Form } from 'antd';
import { useState } from 'react';


function SignToProf() {
  let history = useHistory()
  const [form] = Form.useForm();
  const [phone, setPhone] = useState("")
  const [snils, setSnils] = useState("")  
  function Authorization() {
    axios.get(`https://jsonplaceholder.typicode.com/users?phone=${phone}&snils=${snils}`)
    .then(res => res.data ? history.push(`/anketa?member=${phone}`) : "")
  }

  return (
    <div className="App"> 
     <Link to="/" className="App-header" title="на главную"></Link>     
      <h2>Войти для изменения анкеты</h2>
          <div className="main">
          <Form layout="vertical" onFinish={Authorization} form={form}>
            <Form.Item label="* Телефон (10 цифр)">
              <Input 
                placeholder="XXXXXXXXXX" 
                title="10 цифр без пробелов"
                addonBefore="+7"
                required
                value={phone.replace(/(\d\d\d)(\d\d\d)(\d\d)(\d\d)/g,'$1-$2-$3-$4')}
                pattern="[0-9^а-я]{3}-[0-9^а-я]{3}-[0-9^a-z]{2}-[0-9]{2}"                      
                onChange={(e)=>setPhone(e.target.value)}
              />
            </Form.Item>
            <Form.Item label="* СНИЛС (11 цифр)" >
              <Input 
                required
                placeholder="XXXXXXXXXXX"
                title="11 цифр без пробелов"
                value={snils.replace(/(\d\d\d)(\d\d\d)(\d\d\d)(\d\d)/g,'$1-$2-$3 $4')}
                type="password"
                pattern="[0-9^\w]{3}-[0-9]{3}-[0-9]{3} [0-9]{2}"                       
                onChange={(e)=>setSnils(e.target.value)}
              />
            </Form.Item>        
          <Button type="primary" block htmlType="submit">Войти</Button>
          </Form>        
        </div>    
    </div>
  );
}

export default SignToProf;
